
create table users("id" serial not null primary key, "login" varchar(255) not null, "password" varchar(255) not null, "salt" bytea not null, "email" varchar(255) not null, "region" varchar(255), "first_name" varchar(255), "last_name" varchar(255), "date" Date not null, "year_st" integer not null, "gender" varchar(6) not null, "car_mark" varchar(255) not null);
create table templates ("id" serial not null primary key, "type" smallint not null, "name" varchar(255) not null, "id_user" integer not null references "users"("id"), "views" integer not null, "url_image" varchar(255) not null, "page" varchar, "date_temp" date not null);
create table auto_brands("name" varchar(20) not null primary key, "url" varchar(255) not null);
create table auto_model("name" varchar(100) not null primary key, "name_brand" varchar(20) not null references "auto_brands"("name"), "body" varchar(30) not null, "year" integer not null, "url" varchar(255) not null, "text" varchar not null, "image_url_1" varchar not null, "image_url_2" varchar not null, "image_url_3" varchar not null, "image_url_4" varchar not null, "image_url_5" varchar not null);
create table auto_submodels("name" varchar(200) not null primary key, "name_model" varchar(100) references "auto_model"("name"), "name_brand" varchar(20) not null references "auto_brands"("name"), "price" integer not null, "speed" integer not null, "acceleration" double precision, "capacity" integer not null, "power" integer not null, "flowrate" integer not null, "url" varchar(255) not null);
create table auto_modify("name" varchar(200) not null primary key, "name_model" varchar(100) references "auto_model"("name"), "name_brand" varchar(20) not null references "auto_brands"("name"), "size" varchar not null, "clearence" integer not null, "weight" integer not null, "drive" varchar not null, "engine_type" varchar not null, "power" varchar not null, "capacity" integer not null, "torque" varchar not null, "acceleration" double precision, "fuel" varchar not null, "gearBox" varchar not null, "tires" varchar not null, "disk" varchar not null);
create table ads("id" serial not null primary key, "text" varchar not null, "id_user" integer not null references "users"("id"), "name" varchar not null, "year" integer not null, "url_image_1" varchar not null,  "url_image_2" varchar not null,"url_image_3" varchar not null,"url_image_4" varchar not null,"url_image_5" varchar not null, "price" integer not null, "city" varchar not null, "probeg" integer not null);
